function carTypeInit(a){if(a==="所有车辆"){document.getElementById("allCar").className="list-group-item active"}else{if(a==="越野车"){document.getElementById("SUV").className="list-group-item active"}else{if(a==="小轿车"){document.getElementById("car").className="list-group-item active"}else{if(a==="跑车"){document.getElementById("sportCar").className="list-group-item active"}else{if(a==="超级跑车"){document.getElementById("superCar").className="list-group-item active"}}}}}}function countDays(){let p=document.getElementById("rentalDays");let startDay=document.getElementById("getCarDate").value;let endDay=document.getElementById("returnCarDate").value;let days=getNumberOfDays(startDay,endDay);p.innerText=days;let price=document.getElementById("price").innerText;let totalAmount=document.getElementById("totalAmount");if(days>0&&getNumberOfDays(startDay,getDay(0))<0){document.getElementById("submitBtn").disabled=""}else{if(days<=0){startDay=endDay="还车日期不能早于或等于取车日期";document.getElementById("submitBtn").disabled="disabled"}else{if(startDay===getDay(0)){startDay=endDay="取车日期不能是今天";document.getElementById("submitBtn").disabled="disabled"}else{if(getNumberOfDays(startDay,getDay(0))>0){startDay=endDay="取车日期不能早于今天";document.getElementById("submitBtn").disabled="disabled"}}}}totalAmount.innerText=accMultiply(days,price);document.getElementById("totalAmountInput").value=accMultiply(days,price);document.getElementById("inputGetCarDate").value=startDay;document.getElementById("inputReturnCarDate").value=endDay;document.getElementById("showGetCarDate").innerText=startDay;document.getElementById("showReturnCarDate").innerText=endDay};